const results = [];

for (const item of $input.all()) {
  const query = item.json.query || {};
  const rawPublishedAt = query.published_at;
  const publishedAt = rawPublishedAt && rawPublishedAt !== 'N/A' && !isNaN(new Date(rawPublishedAt).getTime()) ? new Date(rawPublishedAt).toISOString() : null;

  results.push({
    url: query.url || 'N/A',
    search_query: query.search_query || 'N/A',
    site: query.site || 'N/A',
    title: query.title || 'N/A',
    author: query.author || 'Unknown',
    published_at: publishedAt,
    image_url: query.image_url || 'N/A',
    article_html: query.article_html || 'N/A',
    word_count: query.word_count || 0,
    description: query.description || 'N/A'
  });
}

return results.map(item => ({ json: item }));

